mapper.select:
SELECT U_INTG_ID ,U_NICKNAME ,U_LAST_LOGIN_IP ,U_LAST_LOGIN_DTTM ,U_LAST_TERMINAL_KIND ,U_LOGIN_CATE ,U_STATE_CD ,CD1.CD_DTL_NAME AS U_STATE_NM ,U_TYPE_CD ,CD2.CD_DTL_NAME AS U_TYPE_NM ,U_REG_ID ,U_REG_DATE ,U_MOD_ID ,U_MOD_DATE FROM USER U LEFT JOIN CODE_DTL CD1 ON(CD1.CD_DTL_PARENT_ID = 106 AND CD1.CD_DTL_ID = U.U_STATE_CD) LEFT JOIN CODE_DTL CD2 ON(CD2.CD_DTL_PARENT_ID = 107 AND CD2.CD_DTL_ID = U.U_TYPE_CD)
AND U_INTG_ID LIKE CONCAT('%',#{searchId},'%')
AND U_STATE_CD = #{searchState}
AND U_TYPE_CD = #{searchType}
ORDER BY U_LAST_LOGIN_DTTM DESC

mapper.update:
UPDATE USER
   SET U_MOD_ID='SYSTEM'
   	  ,U_MOD_DATE=SYSDATE()

	   	  ,U_STATE_CD=#{val}


	   	  ,U_TYPE_CD=#{val}

 WHERE U_INTG_ID=#{id}
