mapper.insert:

UPDATE CODE 
SET C_DESC = #{CD_DTL_NAME} 
WHERE C_ID = (
					SELECT C_ID 
					FROM CODE 
					WHERE C_DESC =(
										SELECT CD_DTL_NAME 
										FROM CODE_DTL 
										WHERE CD_DTL_ID = #{CD_DTL_ID} 

										)
				)

UPDATE CODE_DTL SET
	 CD_DTL_NAME=#{CD_DTL_NAME, javaType=String, jdbcType=VARCHAR}
	,CD_DTL_DESC=#{CD_DTL_DESC, javaType=String, jdbcType=VARCHAR}
	,CD_DTL_MOD_ID = 'ADMIN'
	,CD_DTL_MOD_DATE = NOW()
WHERE CD_DTL_ID=#{CD_DTL_ID}

mapper.select:
SELECT C_ID
 FROM CODE 
 WHERE C_DESC =#{underOptionIndex}
