mapper.select:
		SELECT CONCAT(IFNULL(A.previousDate, 'isNull'), '@', IFNULL(B.laterDate, 'isNull')) as PreviousLaterDate
          FROM (SELECT MAX(M_MATCHING_DATE) as previousDate FROM MATCHING WHERE M_MATCHING_DATE < #{baseDate} AND M_MY_ID = #{myID}) A,
               (SELECT MIN(M_MATCHING_DATE) as laterDate FROM MATCHING WHERE M_MATCHING_DATE > #{baseDate} AND M_MY_ID = #{myID}) B;
